sourcesContent:
import { resolveInclude } from "ejs";
import db from "../models/index"
import bcrypt from 'bcryptjs';
import { where } from "sequelize";
import { checkPhoneNumberExists, checkUserNameExists } from "./validators";
import { Op } from 'sequelize';
import validator from 'validator';
import jwt from 'jsonwebtoken';

const loginUser = async (identifier, password) => {
   try {
      console.log("Attempting login with identifier:", identifier);  // Log identifier (phone number or email)

      const user = await db.User.findOne({
         where: { phoneNumber: identifier },
         attributes: ['id', 'userName', 'email', 'phoneNumber', 'roleId', 'password'],
         include: [{ model: db.Role, attributes: ['name'] }]
      });

      if (!user) {
         console.log("User not found");
         return { errCode: 1, errMessage: "Wrong account or password!" };
      }

      console.log("User found:", user);  // Log the user object found in DB

      const match = bcrypt.compareSync(password, user.password);
      if (!match) {
         console.log("Password does not match");
         return { errCode: 3, errMessage: "Sai mật khẩu!" };
      }

      // Tạo JWT token
      const token = jwt.sign(
         { id: user.id, roleId: user.roleId },
         process.env.JWT_SECRET,
         { expiresIn: '1h' }
      );

      console.log("Generated JWT token:", token);  // Log the generated token

      const userPlain = user.get({ plain: true });
      delete userPlain.password;
      delete userPlain.Role;

      // Trả về thông tin người dùng và token
      return {
         errCode: 0,
         errMessage: "OK",
         token,  // Trả về token
         user: userPlain
      };

   } catch (err) {
      console.error("Login error:", err);  // Log any errors that occur during the process
      return { errCode: -1, errMessage: "Internal server error" };
   }
};


let registerUser = (data) => {
   return new Promise(async (resolve, reject) => {
      try {
         const validRole = await db.Role.findOne({ where: { id: parseInt(data.roleId) } });
         if (!validRole) {
            return resolve({ errCode: 5, errMessage: 'Invalid roleId' });
         }

         if (!data.phoneNumber || !data.password) {
            return resolve({ errCode: 6, errMessage: 'Phone number and password are required' });
         }

         const errors = [];
         if (await checkPhoneNumberExists(data.phoneNumber)) {
            errors.push("Số điện thoại đã tồn tại!");
         }

         if (errors.length > 0) {
            return resolve({
               errCode: 1,
               errMessage: errors.join(", "),
            });
         }

         let hashPassword = await hashUserPassword(data.password);

         // Tạo người dùng mới
         await db.User.create({
            phoneNumber: data.phoneNumber,
            password: hashPassword,
            firstName: data.firstName || null,
            lastName: data.lastName || null,
            address: data.address || null,
            gender: data.gender || null,
            roleId: parseInt(data.roleId) || 2
         });

         resolve({
            errCode: 0,
            message: 'Tạo tài khoản thành công!'
         });
      } catch (error) {
         reject(error);
      }
   });
};



const hashUserPassword = async (password) => {
   try {
      const salt = await bcrypt.genSalt(10);
      const hashed = await bcrypt.hash(password, salt);
      return hashed;
   } catch (e) {
      throw e;
   }
};

export default {
   loginUser,
   registerUser,
}

